mapper.select:
select
	meal_no
	, pt_no
	, today
	, typeeat
	, mealtime
	, whateat
from
	meal
where
	pt_no = #{pt_no}
	and today = #{today}
	and typeEat = #{typeEat}
	and mealTime = #{mealTime}
	and whatEat = #{whatEat}
	and deleteflag = 'Y'

select
	bedtime_no
	, pt_no
	, today
	, timetosleep
	, timetogetup
	, report
from
	bedtime
where
	pt_no = #{pt_no}
	and today = #{today}
	and timeToSleep = #{timeToSleep}
	and timeToGetup = #{timeToGetup}
	and report = #{report}
	and deleteflag = 'Y'

mapper.update:
update
	meal
set
	typeEat = #{typeEat}
	, mealTime = #{mealTime}
	, whatEat = #{whatEat}
	, updatedate = #{today}
where
	pt_no = #{pt_no}
	and today = #{today}
	and meal_no = #{meal_No}

update
	exercise
set
	exerciseText = #{exerciseText}
	, exerciseTime = #{exerciseTime}
	,updatedate=sysdate
where
	pt_no = #{pt_no}
	and today = #{today}
	and exercise_no = #{exercise_No}

update
	dailycleaning
set
	cleaning = #{cleaning}
	, cleaningReport = #{cleaningReport}
	,updatedate=sysdate
where
	pt_no = #{pt_no}
	and today = #{today}
	and dailycleaning_no = #{dailycleaning_No}

update
	dailywash
set
	wash = #{wash}
	, washReport = #{washReport}
	,updatedate=sysdate
where
	pt_no = #{pt_no}
	and today = #{today}
	and dailywash_no = #{dailywash_No}

update
	dailyshower
set
	shower = #{shower}
	, showerReport = #{showerReport}
	,updatedate=sysdate
where
	pt_no = #{pt_no}
	and today = #{today}
	and dailyshower_No = #{dailyshower_No}

update
	bedtime
set
	timeToSleep = #{timeToSleep}
	, timeToGetup = #{timeToGetup}
	, report = #{report}
	,updatedate=sysdate
where
	pt_no = #{pt_no}
	and today = #{today}
	and bedtime_No = #{bedtime_No}

update
	dr_op
set
