mapper.update:
update patient 
	set
	NURSE_NO=#{nurse_no}
	,NAME=#{name}
	,DISEASE=#{disease}
	,PHONE=#{phone}
	,ADDRESS=#{address}
	,ORIGINALPHOTO=#{originalphoto}
 	,SAVEDPHOTO=#{savedphoto} 	
	,ROOM_NO=#{room_no}
	,PPT_ID=#{ppt_id}
	,PPT_PW=#{ppt_pw}
	,PPT_NAME=#{ppt_name}
	,PPT_PHONE=#{ppt_phone}
	,PPT_ADD=#{ppt_add}	
	
	where PT_NO=#{pt_no}
